    55: 'modern_avatars/abstract/avatar_sandwich.png'
  }
  const file = mapIdToFile[avatarId]
  return `https://raw.githubusercontent.com/chromium/chromium/refs/heads/main/chrome/app/theme/default_100_percent/common/${file}`
