let { obsidiandir, write } = await db('obsidiandir')

let route =
  obsidiandir.length > 0
    ? await arg(
        {
          placeholder: 'Select Obsidian vault',
          shortcuts: [
            {
              name: 'Cancel',
              key: 'escape',
              bar: 'right',
              onPress: () => mainScript(),
              visible: true
            }
          ],
          onNoChoices: async input => {
